import React, { useEffect, useState, useRef } from "react";
import L from "leaflet";

export default function MapView({ onMapChange }) {
  const mapRef = useRef(null);
  const [config, setConfig] = useState(null);
  const [players, setPlayers] = useState([]);
  const [ws, setWs] = useState(null);
  const [uploading, setUploading] = useState(false);

  const playerIcon = (name) =>
    L.divIcon({
      className: "player-icon",
      html: `<div style="
        background-image:url('https://mc-heads.net/avatar/${name}/32');
        width:32px; height:32px;
        border-radius:50%;
        border: 2px solid white;
        background-size: cover;
      "></div>`,
      iconSize: [32, 32],
    });

  useEffect(() => {
    fetch("/map/config")
      .then((res) => res.json())
      .then(setConfig)
      .catch(console.error);
  }, [uploading]);

  useEffect(() => {
    if (!config) return;

    if (mapRef.current) {
      mapRef.current.remove();
      mapRef.current = null;
    }

    const { top_left, bottom_right } = config;
    const bounds = [
      [bottom_right[1], top_left[0]], 
      [top_left[1], bottom_right[0]], 
    ];

    const map = L.map("map", {
      crs: L.CRS.Simple,
      minZoom: -5,
      maxZoom: 5,
      zoomControl: true,
    });

    const imageUrl = `/maps/map.png`;

    L.imageOverlay(imageUrl, bounds).addTo(map);
    map.fitBounds(bounds);

    mapRef.current = map;

    setPlayers([]); 
    if (ws) {
      ws.close();
    }

    const socket = new WebSocket(`ws://${window.location.hostname}:8000/ws`);

    socket.onopen = () => {
      console.log("WebSocket connected");
    };

    socket.onmessage = (event) => {
      try {
        const data = JSON.parse(event.data);
        console.log("WS data received:", data);
        if (data.type === "player_position") {
          setPlayers((prev) => {
            const others = prev.filter((p) => p.name !== data.name);
            return [...others, { name: data.name || "player", x: data.x, z: data.z }];
          });
        }
      } catch (e) {
        console.error(e);
      }
    };


    socket.onclose = () => {
      console.log("WebSocket closed");
    };

    setWs(socket);

    return () => {
      socket.close();
    };
  }, [config]);

  useEffect(() => {
    if (!mapRef.current) return;

    console.log("Players to render:", players);

    mapRef.current.eachLayer((layer) => {
      if (layer.options && layer.options.pane === "markerPane") {
        mapRef.current.removeLayer(layer);
      }
    });

    // Добавляем новых игроков, с учетом правильного порядка координат [Z, X]
    players.forEach(({ name, x, z }) => {
      L.marker([z, x], { icon: playerIcon(name) }).addTo(mapRef.current).bindPopup(name);
    });
  }, [players]);

  async function handleUpload(e) {
    e.preventDefault();
    setUploading(true);

    const form = e.target;
    const file = form.file.files[0];
    const top_left_x = form.top_left_x.value;
    const top_left_z = form.top_left_z.value;
    const bottom_right_x = form.bottom_right_x.value;
    const bottom_right_z = form.bottom_right_z.value;

    if (!file || !top_left_x || !top_left_z || !bottom_right_x || !bottom_right_z) {
      alert("Все поля должны быть заполнены");
      setUploading(false);
      return;
    }

    const data = new FormData();
    data.append("file", file);

    try {
      let res = await fetch("/map/upload", {
        method: "POST",
        body: data,
      });
      let json = await res.json();
      if (json.status !== "ok") throw new Error("Ошибка загрузки файла");

      res = await fetch("/map/set_bounds", {
        method: "POST",
        headers: { "Content-Type": "application/x-www-form-urlencoded" },
        body: new URLSearchParams({
          top_left_x,
          top_left_z,
          bottom_right_x,
          bottom_right_z,
        }),
      });
      json = await res.json();
      if (json.status !== "ok") throw new Error("Ошибка установки границ");

      alert("Карта обновлена!");
      onMapChange();
    } catch (err) {
      alert("Ошибка: " + err.message);
    }

    setUploading(false);
  }

  return (
    <>
      <div id="map" style={{ height: "70vh", width: "100%" }}></div>

      <form onSubmit={handleUpload} style={{ marginTop: 20, padding: 10 }}>
        <h3>Загрузить карту и установить границы</h3>
        <div>
          <input type="file" name="file" accept="image/*" />
        </div>
        <div>
          <label>
            Верхний левый X: <input type="number" name="top_left_x" step="0.1" />
          </label>
        </div>
        <div>
          <label>
            Верхний левый Z: <input type="number" name="top_left_z" step="0.1" />
          </label>
        </div>
        <div>
          <label>
            Нижний правый X: <input type="number" name="bottom_right_x" step="0.1" />
          </label>
        </div>
        <div>
          <label>
            Нижний правый Z: <input type="number" name="bottom_right_z" step="0.1" />
          </label>
        </div>
        <button disabled={uploading} type="submit">
          {uploading ? "Загрузка..." : "Обновить карту"}
        </button>
      </form>
    </>
  );
}
