import React, { useEffect, useState, useRef } from "react";
import L from "leaflet";

export default function MapView() {
  const mapRef = useRef(null);
  const [config, setConfig] = useState(null);
  const [players, setPlayers] = useState([]);
  const [ws, setWs] = useState(null);
  const [uploading, setUploading] = useState(false);
  const [cursorCoords, setCursorCoords] = useState(null);
  const [refreshCounter, setRefreshCounter] = useState(0);

  const imgWidth = 1280;
  const imgHeight = 634;

  const playerIcon = (name) =>
    L.divIcon({
      className: "player-icon",
      html: `<div style="
        background-image:url('https://mc-heads.net/avatar/${name}/32');
        width:32px; height:32px;
        border-radius:50%;
        border: 2px solid white;
        background-size: cover;
      "></div>`,
      iconSize: [32, 32],
    });

  // Координаты Minecraft -> пиксели изображения
  function mcToImageCoords(x, z) {
    if (!config) return [0, 0];

    const minX = Math.min(config.top_left[0], config.bottom_right[0]);
    const maxX = Math.max(config.top_left[0], config.bottom_right[0]);
    const minZ = Math.min(config.top_left[1], config.bottom_right[1]);
    const maxZ = Math.max(config.top_left[1], config.bottom_right[1]);

    const normX = (x - minX) / (maxX - minX);
    const normZ = (z - minZ) / (maxZ - minZ);

    const px = normX * imgWidth;
    const py = (1 - normZ) * imgHeight;

    return [px, py];
  }

  // Пиксели изображения -> координаты Minecraft
  function imageCoordsToMC(px, py) {
    if (!config) return null;

    const minX = Math.min(config.top_left[0], config.bottom_right[0]);
    const maxX = Math.max(config.top_left[0], config.bottom_right[0]);
    const minZ = Math.min(config.top_left[1], config.bottom_right[1]);
    const maxZ = Math.max(config.top_left[1], config.bottom_right[1]);

    const normX = px / imgWidth;
    const normZ = 1 - py / imgHeight;

    const x = minX + normX * (maxX - minX);
    const z = minZ + normZ * (maxZ - minZ);

    return { x: x.toFixed(1), z: z.toFixed(1) };
  }

  // Получение конфигурации карты
  useEffect(() => {
    fetch("/map/config")
      .then((res) => res.json())
      .then(setConfig)
      .catch(console.error);
  }, [refreshCounter]);

  // Инициализация карты и веб-сокета при изменении config
  useEffect(() => {
    if (!config) return;

    if (mapRef.current) {
      mapRef.current.off(); // Отписываем все обработчики
      mapRef.current.remove();
      mapRef.current = null;
    }

    const bounds = [
      [0, 0],
      [imgHeight, imgWidth],
    ];

    const map = L.map("map", {
      crs: L.CRS.Simple,
      minZoom: -5,
      maxZoom: 5,
      zoomControl: true,
    });

    const imageUrl = `/maps/map.png`;

    L.imageOverlay(imageUrl, bounds).addTo(map);
    map.fitBounds(bounds);

    mapRef.current = map;

    setPlayers([]);
    if (ws) {
      ws.close();
      setWs(null);
    }

    const socket = new WebSocket(`ws://${window.location.hostname}:8000/ws`);

    socket.onopen = () => {
      console.log("WebSocket connected");
    };

    socket.onmessage = (event) => {
      try {
        const data = JSON.parse(event.data);
        if (data.type === "player_position") {
          setPlayers((prev) => {
            const others = prev.filter((p) => p.name !== data.name);
            return [...others, { name: data.name || "player", x: data.x, z: data.z }];
          });
        }
      } catch (e) {
        console.error(e);
      }
    };

    socket.onclose = () => {
      console.log("WebSocket closed");
    };

    // Отслеживаем движение мыши по карте
    map.on("mousemove", (e) => {
      if (!config) return;
      const { lat, lng } = e.latlng;
      const mcCoords = imageCoordsToMC(lng, lat);
      if (mcCoords) {
        setCursorCoords(mcCoords);
      } else {
        setCursorCoords(null);
      }
    });

    setWs(socket);

    return () => {
      socket.close();
      map.off();
      map.remove();
    };
  }, [config]);

  // Отрисовка игроков на карте
  useEffect(() => {
    if (!mapRef.current) return;

    mapRef.current.eachLayer((layer) => {
      if (layer.options && layer.options.pane === "markerPane") {
        mapRef.current.removeLayer(layer);
      }
    });

    players.forEach(({ name, x, z }) => {
      const [px, py] = mcToImageCoords(x, z);
      L.marker([py, px], { icon: playerIcon(name) })
        .addTo(mapRef.current)
        .bindPopup(name);
    });
  }, [players, config]);

  // Обработчик загрузки карты
  async function handleUpload(e) {
    e.preventDefault();
    setUploading(true);

    const form = e.target;
    const file = form.file.files[0];
    const top_left_x = form.top_left_x.value;
    const top_left_z = form.top_left_z.value;
    const bottom_right_x = form.bottom_right_x.value;
    const bottom_right_z = form.bottom_right_z.value;

    if (!file || !top_left_x || !top_left_z || !bottom_right_x || !bottom_right_z) {
      alert("Все поля должны быть заполнены");
      setUploading(false);
      return;
    }

    const data = new FormData();
    data.append("file", file);

    try {
      let res = await fetch("/map/upload", {
        method: "POST",
        body: data,
      });
      let json = await res.json();
      if (json.status !== "ok") throw new Error("Ошибка загрузки файла");

      res = await fetch("/map/set_bounds", {
        method: "POST",
        headers: { "Content-Type": "application/x-www-form-urlencoded" },
        body: new URLSearchParams({
          top_left_x,
          top_left_z,
          bottom_right_x,
          bottom_right_z,
        }),
      });
      json = await res.json();
      if (json.status !== "ok") throw new Error("Ошибка установки границ");

      alert("Карта обновлена!");
      setRefreshCounter((c) => c + 1); // обновляем конфиг и карту
    } catch (err) {
      alert("Ошибка: " + err.message);
    }

    setUploading(false);
  }

  return (
    <>
      <div id="map" style={{ height: "70vh", width: "100%" }}></div>

      {cursorCoords && (
        <div style={{ marginTop: 10 }}>
          Координаты Minecraft: X = {cursorCoords.x}, Z = {cursorCoords.z}
        </div>
      )}

      <form onSubmit={handleUpload} style={{ marginTop: 20, padding: 10 }}>
        <h3>Загрузить карту и установить границы</h3>
        <div>
          <input type="file" name="file" accept="image/*" />
        </div>
        <div>
          <label>
            Верхний левый X: <input type="number" name="top_left_x" step="0.1" />
          </label>
        </div>
        <div>
          <label>
            Верхний левый Z: <input type="number" name="top_left_z" step="0.1" />
          </label>
        </div>
        <div>
          <label>
            Нижний правый X: <input type="number" name="bottom_right_x" step="0.1" />
          </label>
        </div>
        <div>
          <label>
            Нижний правый Z: <input type="number" name="bottom_right_z" step="0.1" />
          </label>
        </div>
        <button disabled={uploading} type="submit">
          {uploading ? "Загрузка..." : "Обновить карту"}
        </button>
      </form>
    </>
  );
}
